package main

import (
	"encoding/hex"
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestSliceTx(t *testing.T) {

	prefixBytes, _ := hex.DecodeString("0200000001")
	prevTxnIdBytes, _ := hex.DecodeString("faf3013aab53ae122e6cfdef7720c7a785fed4ce7f8f3dd19379f31e62651c71")
	postFixBytes, _ := hex.DecodeString("")
	fullTxBytes, _ := hex.DecodeString("")

	slicePrefix, sliceTxId, slicePostFix, err := SliceTx(fullTxBytes, 0)

	assert.NoError(t, err)

	assert.ElementsMatch(t, prefixBytes, slicePrefix)
	assert.ElementsMatch(t, prevTxnIdBytes, sliceTxId)
	assert.ElementsMatch(t, postFixBytes, slicePostFix)

}

func TestSliceTxErrorIndex(t *testing.T) {

	fullTxBytes, _ := hex.DecodeString("")

	_, _, _, err := SliceTx(fullTxBytes, 1)

	assert.Error(t, err)

}
